package com.myfatoorahreactnative.googlepay

import com.facebook.react.bridge.ReactApplicationContext
import com.facebook.react.bridge.ReactContextBaseJavaModule
import com.google.android.gms.wallet.button.ButtonConstants

class GooglePayButtonConstantsModule(reactContext: ReactApplicationContext) : ReactContextBaseJavaModule(reactContext) {
  override fun getConstants(): Map<String, Any?> {
    val themes: Map<String, Int> = mapOf(
      "Dark" to ButtonConstants.ButtonTheme.DARK,
      "Light" to ButtonConstants.ButtonTheme.LIGHT
    )

    val types: Map<String, Int> = mapOf(
      "Book" to ButtonConstants.ButtonType.BOOK,
      "Buy" to ButtonConstants.ButtonType.BUY,
      "Checkout" to ButtonConstants.ButtonType.CHECKOUT,
      "Donate" to ButtonConstants.ButtonType.DONATE,
      "Order" to ButtonConstants.ButtonType.ORDER,
      "Pay" to ButtonConstants.ButtonType.PAY,
      "Plain" to ButtonConstants.ButtonType.PLAIN,
      "Subscribe" to ButtonConstants.ButtonType.SUBSCRIBE
    )

    return mapOf(
      "Themes" to themes,
      "Types" to types
    )
  }

  override fun getName() = NAME

  companion object {
    @JvmField
    val NAME = "GooglePayButtonConstants"
  }
}
