import Foundation

struct MFHelper {
    static let onCardBinChangedEventName = "onCardBinChanged"
    static let invoiceIdEventName = "onInvoiceCreated"
    static let applePayLoaded = "applePayLoaded"
    
    static func getColor(number: NSNumber) -> UIColor {
        let cgColor = number.uint32Value
        
        let red = CGFloat((cgColor & 0xFF0000) >> 16) / 255.0
        let green = CGFloat((cgColor & 0x00FF00) >> 8) / 255.0
        let blue = CGFloat(cgColor & 0x0000FF) / 255.0
        let alpha = CGFloat(1.0)
        
        return UIColor(red: red, green: green, blue: blue, alpha: alpha)
    }
    
    static func getColor(number: Int) -> UIColor {
        let cgColor = UInt32(number)
        
        let red = CGFloat((cgColor & 0xFF0000) >> 16) / 255.0
        let green = CGFloat((cgColor & 0x00FF00) >> 8) / 255.0
        let blue = CGFloat(cgColor & 0x0000FF) / 255.0
        let alpha = CGFloat(1.0)
        
        return UIColor(red: red, green: green, blue: blue, alpha: alpha)
    }
}
